import React from 'react';

export const FirebaseDatabase =()=>{
   var Email,Name,Message

   var firebaseConfig = {
      apiKey: "AIzaSyAVoNTgxYnrBr-FGjJw72AkNGxv0xoL9hQ",
      authDomain: "nubaydesployanddatabase.firebaseapp.com",
      projectId: "nubaydesployanddatabase",
      storageBucket: "nubaydesployanddatabase.appspot.com",
      messagingSenderId: "281399404586",
      appId: "1:281399404586:web:af1b4bf7c6fbab8da4fb53"
    };
    // Initialize Firebase
    var firebase;
    firebase.initializeApp(firebaseConfig);

    function Ready(){
       Name=document.getElementById('name').value;    
       Email=document.getElementById('name').value;
       Message=document.getElementById('name').value;
   }

   document.getElementById('Submit').onclick = function(){
      Ready();
      firebase.database().ref('Messages/'+Name).set({
         NameOfTheMessanger: Name,
         EmailOfTheMessanger: Email,
         MessageFromTheMessanger: Message
      });
   }
}
